mapper.select:
SELECT A.MSG_PK AS msgPk, A.MSG_GRP_CD AS msgGrpCd, A.MSG_CD AS msgCd, A.MSG_NM AS msgNm FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT MSG_PK, MSG_GRP_CD, MSG_CD, MSG_NM FROM MESSAGES WHERE 1 = 1
AND MSG_GRP_CD = #{msgGrpCd}
AND MSG_NM = #{msgNm}
AND MSG_CD IN
'${msgCdArray[index]}'
) A WHERE ROWNUM <= #{offset} + #{limit} ) A WHERE RNUM > #{offset}


    SELECT MSG_LOC_PK AS msgLocPk,
           MSG_GRP_CD AS msgGrpCd,
           MSG_CD AS msgCd,
           LANG_CD AS langCd,
           MSG_NM AS msgNm
      FROM MESSAGE_LOCALE
     WHERE MSG_GRP_CD = #{msgGrpCd}
       AND MSG_CD = #{msgCd}
       AND LANG_CD = #{langCd}

mapper.delete:

    DELETE MESSAGE_LOCALE
     WHERE MSG_GRP_CD = #{msgGrpCd}
       AND MSG_CD = #{msgCd}
