mapper.select:
SELECT A.NOTICE_ID AS noticeId, A.TITLE AS title, A.CONTENT AS content, A.OPEN_YMD AS openYmd, A.CLOSE_YMD AS closeYmd FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT NOTICE_ID, TITLE, SUBSTR(CONTENT,1,100) AS CONTENT, OPEN_YMD, CLOSE_YMD FROM NOTICES WHERE 1 = 1
AND TITLE LIKE '%'||#{title}||'%'
AND CONTENT LIKE '%'||#{content}||'%'
) A WHERE ROWNUM <= #{offset} + #{limit} ) A WHERE RNUM > #{offset}


    SELECT A.FILE_NM AS FILE_NM,
           A.FILE_CONTENT AS FILE_CONTENT
      FROM NOTICE_FILE A
     WHERE NOTICE_ID = #{noticeId}
       AND SEQ = #{seq}

mapper.update:

    UPDATE NOTICE_FILE
       SET FILE_NM = #{fileNm},
           FILE_URL = #{fileUrl},
           FILE_CONTENT = #{fileContent},
           UPD_DATE = SYSDATE,
           UPD_USER_ID = #{sessionUserId}
     WHERE NOTICE_ID = #{noticeId}
       AND SEQ = #{seq}

mapper.delete:

    DELETE NOTICE_FILE
     WHERE NOTICE_ID = #{noticeId}
       AND SEQ = #{seq}
