mapper.select:
SELECT A.RESOURCE_ID AS resourceId, A.RESOURCE_NM AS resourceNm, A.RESOURCE_TYPE AS resourceType, A.RESOURCE_URL AS resourceUrl, A.LINK_RESOURCE_ID AS linkResourceId, A.USE_YN AS useYn FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT RESOURCE_ID, RESOURCE_NM, RESOURCE_TYPE, RESOURCE_URL, LINK_RESOURCE_ID, USE_YN FROM RESOURCES WHERE 1 = 1
AND RESOURCE_NM = #{resourceNm}
AND RESOURCE_TYPE = #{resourceType}
AND RESOURCE_URL LIKE #{resourceUrl}||'%'
AND LINK_RESOURCE_ID = #{linkResourceId}
AND USE_YN = #{useYn}
) A WHERE ROWNUM <= #{offset} + #{limit} ) A WHERE RNUM > #{offset}
